// Header.js
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faCog } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './styles/Header.css';
import '../App.css';

const Header = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [userKeyword, setUserKeyword] = useState("");

  const handleSearch = async () => {
    try {
      // 검색어를 사용하여 서버에 검색 요청
      const response = await axios.get(`http://localhost:5000/search?keyword=${userKeyword}`);
      const searchResultsData = response.data;

      // 검색 결과를 콘솔에 출력
      console.log("Search Results:", searchResultsData);

      // 검색 결과를 state에 업데이트
      setSearchResults(searchResultsData);

      // 추가로 팝업 창 등을 통해 검색 결과를 보여줄 수 있습니다.
    } catch (error) {
      console.error("Error searching:", error);
      // 에러 처리 로직 추가
    }
  };

  return (
    <div className="header-container">
      {/* 로고 */}
      <Link to="/">
        <img src="/images/logo.png" alt="Your Logo" className="logo-image" />
      </Link>

      {/* 검색창과 검색 버튼 */}
      <div className="search-container">
        <input
          type="text"
          placeholder="전체검색창(빠른검색)"
          className="search-input"
          value={userKeyword}
          onChange={(e) => setUserKeyword(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      {/* 검색 결과 표시 부분 */}
      {searchResults.length > 0 && (
        <div className="search-results">
          <h2>Search Results</h2>
          {searchResults.map((result, index) => (
            <div key={index}>
              <p>{result.folderName}</p>
              <ul>
                {result.files.map((file, fileIndex) => (
                  <li key={fileIndex}>{file.filename} - {file.uploadDate}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}

      {/* 로그인 버튼, 달력 버튼, 설정 버튼 */}
      <div className="button-container">
        <button className="icon-button"><FontAwesomeIcon icon={faCalendarAlt} /></button>
        <button className="icon-button"><FontAwesomeIcon icon={faCog} /></button>
        <button className="login-button">로그인</button>
      </div>
    </div>
  );
};

export default Header;
