// Header.js

import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faCog } from '@fortawesome/free-solid-svg-icons';
import { Link, useNavigate } from 'react-router-dom';
import './styles/Header.css';
import '../App.css';

const Header = () => {
  const navigate = useNavigate();
  const [searchKeyword, setSearchKeyword] = useState("");
  const [searchResults, setSearchResults] = useState([]); // 추가

  // 검색 결과 요청을 서버에 보내는 함수
  const handleSearchButtonClick = async () => {
    try {
      const response = await fetch(`http://localhost:5000/keyword_search?keyword=${searchKeyword}`);
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  // 검색 결과 클릭 시 해당 파일로 이동하는 함수
  const handleSearchResultClick = (fileId) => {
    navigate(`/file-details/${fileId}`);
  };

  return (
    <div className="header-container">
      {/* 로고 */}
      <Link to="/">
        <img src="/images/logo.png" alt="Your Logo" className="logo-image" />
      </Link>

      {/* 검색창과 검색 버튼 */}
      <div className="search-container">
        <input
          type="text"
          placeholder="전체검색창(빠른검색)"
          className="search-input"
          value={searchKeyword}
          onChange={(e) => setSearchKeyword(e.target.value)}
        />
        <button onClick={handleSearchButtonClick}>검색</button>
      </div>

      {/* 로그인 버튼, 달력 버튼, 설정 버튼 */}
      <div className="button-container">
        <button className="icon-button"><FontAwesomeIcon icon={faCalendarAlt} /></button>
        <button className="icon-button"><FontAwesomeIcon icon={faCog} /></button>
        <button className="login-button">로그인</button>
      </div>

      {/* 검색 결과 표시 */}
      <div className="search-results">
        {searchResults.map((result) => (
          <div key={result._id} onClick={() => handleSearchResultClick(result._id)}>
            {result.filename} - {result.timestamp} {/* 파일 정보에 따라 표시할 내용 조정 */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Header;
