// Header.js

import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faCog } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import './styles/Header.css'
import '../App.css'

const Header = ({ setSearchKeyword }) => {
  // Header 컴포넌트에서 검색어 상태를 관리합니다.
  const [searchValue, setSearchValue] = useState('');

  // 검색어 입력 시 검색어 상태를 업데이트합니다.
  const handleSearchInputChange = (e) => {
    setSearchValue(e.target.value);
  };

  // 검색 버튼 클릭 시 검색어를 전달하고 검색어 상태를 초기화합니다.
  const handleSearchButtonClick = () => {
    setSearchKeyword(searchValue);
    setSearchValue('');
  };

  return (
    <div className="header-container">
      {/* 로고 */}
      <Link to="/">
        <img src="/images/logo.png" alt="Your Logo" className="logo-image" />
      </Link>

      {/* 검색창과 검색 버튼 */}
      <div className="search-container">
        <input
          type="text"
          placeholder="전체검색창(빠른검색)"
          className="search-input"
          value={searchValue}
          onChange={handleSearchInputChange}
        />
        <button onClick={handleSearchButtonClick}>검색</button>
      </div>

      {/* 로그인 버튼, 달력 버튼, 설정 버튼 */}
      <div className="button-container">
        <button className="icon-button"><FontAwesomeIcon icon={faCalendarAlt} /></button>
        <button className="icon-button"><FontAwesomeIcon icon={faCog} /></button>
        <button className="login-button">로그인</button>
      </div>
    </div>
  );
};

export default Header;
