// Header.js
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faCog } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import './styles/Header.css'
import '../App.css'
import axios from 'axios'; // axios 추가

const Header = ({ onSearch }) => {
  const [searchKeyword, setSearchKeyword] = useState('');

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/keyword_search?keyword=${searchKeyword}`);
      const results = response.data;
      onSearch(results); // 부모 컴포넌트로 검색 결과 전달
    } catch (error) {
      console.error('검색 결과를 가져오는 중 에러 발생:', error);
      // 에러 처리
    }
  };

  return (
    <div className="header-container">
      {/* 로고 */}
      <Link to="/">
        <img src="/images/logo.png" alt="Your Logo" className="logo-image" />
      </Link>

      {/* 검색창과 검색 버튼 */}
      <div className="search-container">
        <input
          type="text"
          placeholder="전체검색창(빠른검색)"
          className="search-input"
          value={searchKeyword}
          onChange={(e) => setSearchKeyword(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      {/* 로그인 버튼, 달력 버튼, 설정 버튼 */}
      <div className="button-container">
        <button className="icon-button"><FontAwesomeIcon icon={faCalendarAlt} /></button>
        <button className="icon-button"><FontAwesomeIcon icon={faCog} /></button>
        <button className="login-button">로그인</button>
      </div>
    </div>
  );
};

export default Header;
