// Header.js
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faCog } from '@fortawesome/free-solid-svg-icons';
import { Link, useNavigate } from 'react-router-dom';
import './styles/Header.css';
import '../App.css';

const Header = ({ onSearch }) => {
  const [searchInputValue, setSearchInputValue] = useState('');
  const navigate = useNavigate();

  const handleSearch = () => {
    // 검색어를 기반으로 검색 결과를 가져옵니다.
    // setSearchKeyword가 HomeScreen 컴포넌트에서 상태를 업데이트하는 함수라고 가정합니다.
    onSearch(searchInputValue);
    // 홈 화면 경로로 이동되지 않았다면 이동하도록 설정할 수 있습니다.
    // 예: navigate("/");
  };

  return (
    <div className="header-container">
      {/* 로고 */}
      <Link to="/">
        <img src="/images/logo.png" alt="Your Logo" className="logo-image" />
      </Link>

      {/* 검색창과 검색 버튼 */}
      <div className="search-container">
        <input
          type="text"
          placeholder="전체검색창(빠른검색)"
          className="search-input"
          value={searchInputValue}
          onChange={(e) => setSearchInputValue(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      {/* 로그인 버튼, 달력 버튼, 설정 버튼 */}
      <div className="button-container">
        <button className="icon-button">
          <FontAwesomeIcon icon={faCalendarAlt} />
        </button>
        <button className="icon-button">
          <FontAwesomeIcon icon={faCog} />
        </button>
        <button className="login-button">로그인</button>
      </div>
    </div>
  );
};

export default Header;
