import React, { useState, useEffect } from "react";
import "./styles/HomeScreen.css";
import { useContext } from "react";
import { AppContext } from "../AppContext.js";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import Header from '../components/Header'; 

const HomeScreen = () => {
  const navigate = useNavigate();
  const [searchResults, setSearchResults] = useState([]);
  const {
    folders,
    uploadedFiles,
    addFolder,
    addFileToFolder,
    setUploadedFiles,
  } = useContext(AppContext);
  const [isUploadMenuOpen, setUploadMenuOpen] = useState(false);
  const [addedFolders, setAddedFolders] = useState([
    { name: "최근 업로드 폴더", id: 1 },
  ]);
  const [userKeyword, setUserKeyword] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const [isRenamePopupOpen, setRenamePopupOpen] = useState(false);
  const [newName, setNewName] = useState("");
  const [folderFiles, setFolderFiles] = useState({});
  const [searchKeyword, setSearchKeyword] = useState("");
  const [filteredFilesObj, setFilteredFilesObj] = useState({});

    // 폴더와 파일 필터링
  const filteredFiles = (folderName) =>
    folderFiles[folderName]
      ? folderFiles[folderName].filter((file) =>
          file.filename.toLowerCase().includes(searchKeyword.toLowerCase())
        )
      : [];

  const filteredFoldersAndFiles = addedFolders.filter((folder) =>
    folder.name.toLowerCase().includes(searchKeyword.toLowerCase()) ||
    filteredFiles(folder.name).some((file) =>
      file.filename.toLowerCase().includes(searchKeyword.toLowerCase())
    )
  );

  const handleSearch = async (keyword) => {
    // 검색 결과를 처리하는 로직 추가
    setUserKeyword(keyword);
    setSearchKeyword(keyword);
  };

  useEffect(() => {

    // 파일 필터링
    const calculateFilteredFiles = () => {
      const newFilteredFilesObj = {};
      addedFolders.forEach((folder) => {
        newFilteredFilesObj[folder.name] = filteredFiles(folder.name);
      });
      setFilteredFilesObj(newFilteredFilesObj);
    };
  
    // 초기 로딩 시에도 호출
    calculateFilteredFiles();
  }, [addedFolders, folderFiles, searchKeyword]);


  useEffect(() => {
    // 페이지 로드 시 로컬 스토리지에서 uploadedFiles 값을 가져옴
    const storedUploadedFiles = localStorage.getItem("uploadedFiles");
    if (storedUploadedFiles) {
      setUploadedFiles(JSON.parse(storedUploadedFiles));
    }

    // 로컬 스토리지에서 폴더 정보를 가져옴
    const storedFolders = localStorage.getItem("addedFolders");
    if (storedFolders) {
      setAddedFolders(JSON.parse(storedFolders));
    } else {
      // 초기 폴더 정보 설정
      setAddedFolders([{ name: "최근 업로드 폴더", id: 1 }]);
      localStorage.setItem("addedFolders", JSON.stringify([{ name: "최근 업로드 폴더", id: 1 }]));
    }
  }, [setUploadedFiles, setAddedFolders]);

  useEffect(() => {
    const storedFolderFiles = localStorage.getItem("folderFiles");
    if (storedFolderFiles) {
      setFolderFiles(JSON.parse(storedFolderFiles));
    }

    const storedFolders = localStorage.getItem("addedFolders");
    if (storedFolders) {
      setAddedFolders(JSON.parse(storedFolders));
    } else {
      setAddedFolders([{ name: "최근 업로드 폴더", id: 1 }]);
      localStorage.setItem(
        "addedFolders",
        JSON.stringify([{ name: "최근 업로드 폴더", id: 1 }])
      );
    }
  }, [setFolderFiles, setAddedFolders]);
  
  

  useEffect(() => {
    const handleBeforeUnload = () => {
      localStorage.setItem("uploadedFiles", JSON.stringify(uploadedFiles));
      localStorage.setItem("addedFolders", JSON.stringify(addedFolders));
      localStorage.setItem("folderFiles", JSON.stringify(folderFiles));
    };

    window.addEventListener("beforeunload", handleBeforeUnload);

    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, [uploadedFiles, addedFolders, folderFiles]);

  const [isCreateFolderPopupOpen, setCreateFolderPopupOpen] = useState(false);
  const [newFolderName, setNewFolderName] = useState("");

  const [isFileUploadPopupOpen, setFileUploadPopupOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [customFileName, setCustomFileName] = useState("");
  const [selectedFolder, setSelectedFolder] = useState("");
  const [uploadedFilesState, setUploadedFilesState] = useState({});
  const [folderVisibility, setFolderVisibility] = useState({});

  const handleUploadButtonClick = () => {
    setUploadMenuOpen(!isUploadMenuOpen);
  };

  const handleFileUploadButtonClick = () => {
    setFileUploadPopupOpen(true);
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    try {
      if (!selectedFile || !selectedFolder) {
        console.error("No file selected");
        return;
      }

      const formData = new FormData();
      formData.append("files", selectedFile);
      formData.append("customFileName", customFileName);
      formData.append("selectedFolder", selectedFolder);

      formData.append("uploadDate", new Date().toISOString());
      formData.append("folderName", selectedFolder);

      const response = await axios.post("http://localhost:5000/upload_files", formData);

      //const data = await response.json();
      console.log(response.data.message);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  // 폴더 필터링
  // 폴더 필터링
  const filteredFolders = addedFolders.filter((folder) =>
    folder.name.toLowerCase().includes(searchKeyword.toLowerCase())
  );

  
  const handleSearchButtonClick = () => {
    onSearch(userKeyword);
  };

  const handleFolderIconClick = async (folderName) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/get_files?folder=${encodeURIComponent(folderName)}`
      );
      const files = response.data;
      console.log(files);

      // 클릭한 폴더의 가시성을 토글합니다.
      setFolderVisibility((prevVisibility) => ({
        ...prevVisibility,
        [folderName]: !prevVisibility[folderName],
      }));

      // 파일 목록 상태를 업데이트합니다.
      setFolderFiles((prevFiles) => ({
        ...prevFiles,
        [folderName]: files,
      }));
    } catch (error) {
      console.error('Error fetching files:', error);
      // 에러 처리 로직 추가
    }
  };

  const handleCreateFolderButtonClick = () => {
    setCreateFolderPopupOpen(true);
  };

  const handleCreateFolder = async () => {
    if (newFolderName.trim() !== "") {
      const newFolder = { name: newFolderName, id: Date.now() };
      setAddedFolders((prevFolders) => [...prevFolders, newFolder]);
      localStorage.setItem(
        "addedFolders",
        JSON.stringify([...addedFolders, newFolder])
      );
      addFolder(newFolder);

      // 서버에 새로운 폴더 정보 전송
      try {
        await axios.post("http://localhost:5000/create_folder", {
          folder: newFolder,
        });
      } catch (error) {
        console.error('Error creating folder:', error);
        // 에러 처리 로직 추가
      }

      setNewFolderName("");
    }
    setCreateFolderPopupOpen(false);
  };

  const handleRename = () => {
    if (newName.trim() !== "") {
      const updatedFolders = addedFolders.map((folder) =>
        folder.id === selectedItem.id ? { ...folder, name: newName } : folder
      );
      setAddedFolders(updatedFolders);
      setRenamePopupOpen(false);
      setSelectedItem(null);
    }
  };

  const handleMoveToTrash = () => {
    if (newName.trim() !== "") {
      const updatedFolders = addedFolders.map((folder) =>
        folder.id === selectedItem.id ? { ...folder, name: newName } : folder
      );
      setAddedFolders(updatedFolders);
      setRenamePopupOpen(false);
      setSelectedItem(null);
    }
  };

  return (
    <div>
      {/* Header 컴포넌트를 사용하고 onSearch prop으로 handleSearch 함수를 전달 */}
      <Header onSearch={handleSearch} />
      <input
        type="text"
        placeholder="전체검색창(빠른검색)"
        className="search-input"
        value={userKeyword}
        onChange={(e) => setUserKeyword(e.target.value)}
      />
      <button onClick={handleSearchButtonClick}>검색</button>
  
      {filteredFoldersAndFiles.map((folder) => (
        <div key={folder.id}>
          <div
            className="folder-content"
            onClick={() => handleFolderIconClick(folder.name)}
          >
            <div className="folder-icon img">
              <img
                src="/images/folder-icon.png"
                alt="폴더 아이콘"
                style={{ width: "20px", height: "20px" }}
              />
            </div>
            <div className="folder-name">{folder.name}</div>
          </div>
          {folderVisibility[folder.name] && (
            <div className="uploaded-files-container">
              {filteredFiles(folder.name).map((file, index) => (
                <div key={index} className="uploaded-file">
                  <img
                    src="/images/file-icon.png"
                    alt="File Icon"
                    className="file-icon"
                  />
                  <div className="file-name">{file.filename}</div>
                  <div className="upload-date">{file.uploadDate}</div>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
  


      <button className="upload-button" onClick={handleUploadButtonClick}>
        <img
          src="/images/upload.png"
          alt="업로드 아이콘"
          style={{ width: "40px", height: "35px" }}
        />
      </button>

      {isUploadMenuOpen && (
        <div className="upload-menu">
          <button
            className="upload-menu-button"
            onClick={handleCreateFolderButtonClick}
          >
            폴더 생성
          </button>
          <button
            className="upload-menu-button"
            onClick={handleFileUploadButtonClick}
          >
            파일 업로드
          </button>
        </div>
      )}

      {isCreateFolderPopupOpen && (
        <div className="create-folder-popup">
          <input
            className="create-folder-input"
            type="text"
            placeholder="폴더명을 입력하세요"
            value={newFolderName}
            onChange={(e) => setNewFolderName(e.target.value)}
          />
          <button className="create-folder-button" onClick={handleCreateFolder}>
            폴더 생성
          </button>
        </div>
      )}

      {isFileUploadPopupOpen && (
        <div className="file-upload-popup">
          <input
            className="file-upload-input"
            type="file"
            onChange={handleFileSelect}
          />
          <input
            className="file-upload-custom-input"
            type="text"
            placeholder="파일명을 입력하세요"
            value={customFileName}
            onChange={(e) => setCustomFileName(e.target.value)}
          />
          <select
            className="file-upload-select"
            value={selectedFolder}
            onChange={(e) => setSelectedFolder(e.target.value)}
          >
            <option value="" disabled>
              Select a folder
            </option>
            {addedFolders.map((folder) => (
              <option key={folder.id} value={folder.name}>
                {folder.name}
              </option>
            ))}
          </select>
          <button className="file-upload-button" onClick={handleUpload}>
            업로드
          </button>
        </div>
      )}

      {isRenamePopupOpen && (
        <div className="rename-popup">
          <input
            type="text"
            placeholder="새로운 이름을 입력하세요"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <button onClick={handleRename}>이름 변경</button>
          <button onClick={handleMoveToTrash}>휴지통으로 이동</button>
        </div>
      )}
    </div>
  );
};

export default HomeScreen;
