// Header.js
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faCog } from '@fortawesome/free-solid-svg-icons';
import { Link, useNavigate } from 'react-router-dom';  // useNavigate 추가
import './styles/Header.css'
import '../App.css'

import HomeScreen, { handleNavigateToKeywordResult } from '../components/HomeScreen';

const Header = () => {
  const navigate = useNavigate();
  const [userKeyword, setUserKeyword] = useState("");

  const handleNavigateToKeywordResult = () => {
    // Use the navigate function directly
    navigate(`/keyword-result?keyword=${userKeyword}`);
  };

  return (
    <div className="header-container">
      {/* 로고 */}
      <Link to="/">
        <img src="/images/logo.png" alt="Your Logo" className="logo-image" />
      </Link>

      {/* 검색창과 검색 버튼 */}
      <div className="search-container">
        <input
          type="text"
          placeholder="전체검색창(빠른검색)"
          className="search-input"
          value={userKeyword}
          onChange={(e) => setUserKeyword(e.target.value)}
        />
        <button onClick={handleNavigateToKeywordResult}>검색</button>
      </div>

      {/* 로그인 버튼, 달력 버튼, 설정 버튼 */}
      <div className="button-container">
        <button className="icon-button">
          <FontAwesomeIcon icon={faCalendarAlt} />
        </button>
        <button className="icon-button">
          <FontAwesomeIcon icon={faCog} />
        </button>
        <button className="login-button">로그인</button>
      </div>
    </div>
  );
};

export default Header;
