import React, { useState } from "react";
import axios from "axios";

const KeywordSearchPage = () => {
  const [searchKeyword, setSearchKeyword] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(
        `http://localhost:5000/keyword_search?keyword=${encodeURIComponent(
          searchKeyword
        )}`
      );
      const data = response.data;
      setSearchResults(data);
    } catch (error) {
      setError("검색 중 오류가 발생했습니다.");
    }

    setLoading(false);
  };

  // 검색어 하이라이팅 함수
  const highlightKeyword = (sentence) => {
    if (!searchKeyword.trim()) {
      return sentence; // 검색어가 비어있으면 그대로 반환
    }

    const regex = new RegExp(`(${searchKeyword})`, "gi");
    return sentence.replace(regex, (match, p1) => `<span class="highlight">${p1}</span>`);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="검색어를 입력하세요"
        value={searchKeyword}
        onChange={(e) => setSearchKeyword(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        검색
      </button>

      {loading && <p>검색 중...</p>}
      {error && <p>{error}</p>}

      {searchResults.length > 0 && (
        <div>
          <h2>검색 결과</h2>
          <ul>
            {searchResults.map((result) => (
              <li key={result._id}>
                <p>파일명: {result.filename}</p>
                {/* 키워드 하이라이팅 */}
                <p dangerouslySetInnerHTML={{ __html: `키워드: ${highlightKeyword(result.sentence[0])}` }}></p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default KeywordSearchPage;
